import { Box, Card, CardBody, CardHeader, Center, Heading, Image, Text } from "@chakra-ui/react";

export default function Song() {
    return (
        <Box position="relative" marginX={30} >
            <Card h="68vh" w="40vw" minWidth="205px" minHeight="275px" borderRadius="35px">
                <CardBody >
                    <Center>
                        <Box>
                            <Image
                                minWidth="100px" minHeight="100px"
                                borderRadius='full'
                                height="25vh"
                                width="25vh"
                                src='https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSHhgym4kW-5jjHNHZGCngIruP-9rhgz42PDw'
                            />
                        </Box>
                    </Center>
                    <Center>
                        <Heading as="b" size="xl">Título</Heading>
                    </Center>
                    <Center>
                        <Heading as="b" size="md">Artista</Heading>
                    </Center>
                    <Center >
                        <Box display="block" >
                            <Box display="flex">
                                <Text fontSize='xl' as="b" marginX="5%" css={{
                                    '@media screen and (max-width: 512px)': {
                                        fontSize: "14px"
                                    },
                                }}>Album</Text>
                                <Text fontSize='xl' marginLeft="5%" css={{
                                    '@media screen and (max-width: 512px)': {
                                        fontSize: "14px"
                                    },
                                }}>Nombre del album</Text>
                            </Box>
                            <Box display="flex" >
                                <Text fontSize='xl' as="b" marginX="5%" css={{
                                    '@media screen and (max-width: 512px)': {
                                        fontSize: "14px"
                                    },
                                }}>Año</Text>
                                <Text fontSize='xl' marginLeft="5%" css={{
                                    '@media screen and (max-width: 512px)': {
                                        fontSize: "14px"
                                    },
                                }}>2004</Text>
                            </Box>
                            <Box display="flex" >
                                <Text fontSize='xl' as="b" marginX="5%" css={{
                                    '@media screen and (max-width: 512px)': {
                                        fontSize: "14px"
                                    },
                                }}>Duración</Text>
                                <Text fontSize='xl' marginLeft="5%" css={{
                                    '@media screen and (max-width: 512px)': {
                                        fontSize: "14px"
                                    },
                                }}>3:00</Text>
                            </Box>
                        </Box>
                    </Center>
                </CardBody>
            </Card>
        </Box>
    );
}
